SELECT count(*) as count,SUM( money ) AS money, SUM( rates_money ) AS rates_money, SUM( total_money ) AS total_money, SUM( actual_money ) AS actual_money FROM (SELECT jc_fapiao.* FROM jc_fapiao
LEFT JOIN jc_payment_app ON jc_fapiao.id = jc_payment_app.fapiao LEFT JOIN jc_oa_schedule ON jc_payment_app.SCHEDULE = jc_oa_schedule.id
AND jc_fapiao.bill_no LIKE CONCAT('%',#{condition.bill_no},'%')
AND jc_fapiao.is_add = #{condition.is_add}
AND jc_fapiao.make_name= #{condition.make_name}
AND jc_fapiao.type= #{condition.type}
AND jc_fapiao.enter_name LIKE CONCAT('%',#{condition.enter_name},'%')
AND jc_fapiao.create_date >= CONCAT(#{condition.createrdate_start},' 00:00:00')
AND jc_fapiao.create_date <= CONCAT(#{condition.createrdate_end},' 23:59:59')
AND jc_fapiao.make_date >= CONCAT(#{condition.makedate_start},' 00:00:00')
AND jc_fapiao.make_date <= CONCAT(#{condition.makedate_end},' 23:59:59')
AND jc_fapiao.verificatio_time >= CONCAT(#{condition.verificatio_start},' 00:00:00')
AND jc_fapiao.verificatio_time <= CONCAT(#{condition.verificatio_end},' 23:59:59')
AND jc_fapiao.create_userid = #{condition.create_userid}
AND jc_fapiao.audit_userid = #{condition.audit_userid}
AND jc_fapiao.source=#{condition.source}
AND jc_oa_schedule.creator=#{condition.order_xs}
AND jc_oa_schedule.office_account=(SELECT id from wm_office_account WHERE name LIKE CONCAT('%',#{condition.society},'%'))
AND jc_fapiao.state IN
#{state}
AND is_deleted=0
GROUP BY jc_fapiao.id) a

SELECT jc_fapiao.*, ( SELECT NAME FROM bms_manager WHERE id = jc_fapiao.create_userid ) creator_name, ( SELECT NAME FROM bms_manager WHERE id = jc_fapiao.audit_userid ) audit_username
,IFNULL(( SELECT NAME FROM wm_office_account WHERE id = jc_oa_schedule.office_account ),'')AS society ,IFNULL((jc_oa_schedule.creator ),'') AS sell_id ,IFNULL(( SELECT NAME FROM bms_manager WHERE id = jc_oa_schedule.creator ),'') AS sell_name ,IFNULL( jc_oa_schedule.put_date,'') AS order_time
FROM jc_fapiao
LEFT JOIN jc_payment_app ON jc_fapiao.id = jc_payment_app.fapiao LEFT JOIN jc_oa_schedule ON jc_payment_app.SCHEDULE = jc_oa_schedule.id
AND jc_fapiao.bill_no LIKE CONCAT('%',#{condition.bill_no},'%')
AND jc_fapiao.is_add = #{condition.is_add}
AND jc_fapiao.make_name= #{condition.make_name}
AND jc_fapiao.type= #{condition.type}
AND jc_fapiao.enter_name LIKE CONCAT('%',#{condition.enter_name},'%')
AND jc_fapiao.create_date >= CONCAT(#{condition.createrdate_start},' 00:00:00')
AND jc_fapiao.create_date <= CONCAT(#{condition.createrdate_end},' 23:59:59')
AND jc_fapiao.make_date >= CONCAT(#{condition.makedate_start},' 00:00:00')
AND jc_fapiao.make_date <= CONCAT(#{condition.makedate_end},' 23:59:59')
AND jc_fapiao.verificatio_time >= CONCAT(#{condition.verificatio_start},' 00:00:00')
AND jc_fapiao.verificatio_time <= CONCAT(#{condition.verificatio_end},' 23:59:59')
AND jc_fapiao.create_userid = #{condition.create_userid}
AND jc_fapiao.audit_userid = #{condition.audit_userid}
AND jc_oa_schedule.creator=#{condition.order_xs}
AND jc_oa_schedule.office_account=(SELECT id from wm_office_account WHERE name LIKE CONCAT('%',#{condition.society},'%'))
AND jc_fapiao.state IN
#{state}
AND is_deleted=0
ORDER BY jc_fapiao.id DESC

